#!/bin/bash

mkdir -p ~/.ssh
cd ~/.ssh
if [ ! -e authorized_keys ]; then
  if [ -e authorized_keys2 ]; then
    mv authorized_keys2 authorized_keys
  fi
  touch authorized_keys
fi

grep -qim 1 "# Anth 2016" authorized_keys || cat >>authorized_keys <<EOF

ssh-rsa 
# Anth 2016
EOF

grep -qim 1 "# Dirvish 2013" authorized_keys || cat >>authorized_keys <<EOF

command="rsync --server --sender -vlHogDtprx --numeric-ids . /",no-port-forwarding,no-X11-forwarding,no-agent-forwarding,no-pty ssh-rsa  dirvish-2013
# Dirvish 2013
EOF

grep -qim 1 "# 2015-01-14" authorized_keys || cat >>authorized_keys <<EOF

ssh-rsa 
# 2015-01-14 DLS
EOF


